import { combineReducers } from 'redux';

import { 
    SET_SCORE, 
    SET_ROWS, 
    SET_LEVEL, 
    SET_DROPTIME, 
    SET_GAMEOVER, 
    SET_BTNTITLE,
    SET_IEALERT
 } from '../_actions/types';

const tetris = (state={}, action) => {
    switch(action.type){
        case SET_SCORE:
            return {...state, score: action.payload }
        case SET_ROWS:
            return { ...state, rows: action.payload }
        case SET_LEVEL:
            return {...state, level: action.payload }
        case SET_DROPTIME:
            return {...state, dropTime: action.payload }
        case SET_GAMEOVER:
            return {...state, gameover: action.payload }
        case SET_BTNTITLE:
            return {...state, btnTitle: action.payload }
        case SET_IEALERT:
            return {...state, ieIEAlert: action.payload }
        default:
            return state;
    }
}

const tetrisReducer = combineReducers({
    tetris,
});

export default tetrisReducer;
