import { 
    SET_SCORE, 
    SET_ROWS, 
    SET_LEVEL, 
    SET_DROPTIME, 
    SET_GAMEOVER, 
    SET_BTNTITLE
} from '../_actions/types';

export function setScore(score){
    return {
        type: SET_SCORE,
        payload: score,
    }
}

export function setRows(rows){
    return {
        type: SET_ROWS,
        payload: rows,
    }
}

export function setLevel(level){
    return {
        type: SET_LEVEL,
        payload: level,
    }
}

export function setDropTime(dropTime){
    return {
        type: SET_DROPTIME,
        payload: dropTime,
    }
}

export function setGameOver(gameOver){
    return {
        type: SET_GAMEOVER,
        payload: !gameOver,
    }
}

export function setBtnTitle(btnTitle){
    return {
        type: SET_BTNTITLE,
        payload: btnTitle,
    }
}
