import styled from 'styled-components';

export const StyledButton = styled.button`
    box-sizing: border-box;
    margin: 0 0 20px 0;
    padding: 20px;
    min-height: ${props => (props.type ==='alert' ? '10px' : '30px')};
    width: ${props => (props.type === 'alert' ? '50%' : '100%')};
    border-radius: 20px;
    border: none;
    color: white;
    background: #333;
    font-family: Arial, Helvetica, sans-serif;
    font-size: 1rem;
    outline: none;
    cursor: pointer;
    position: ${props => (props.type === 'alert' && 'fixed')};
    right:  ${props => (props.type === 'alert' && '10px')};
    bottom:  ${props => (props.type === 'alert' && '10px')};
`;
