import { useEffect, useCallback, useMemo } from 'react';

import { useSelector, useDispatch } from 'react-redux';
import * as actions from '../_actions/index';

export const useGameValues = rowsCleared => {
    const dispatch = useDispatch();
    const tetris = useSelector((store) => store.tetris);

    dispatch(actions.setScore(0));
    ispatch(actions.setRows(0));
    ispatch(actions.setLevel(0));

    // original tetris game score
    const linePoints = useMemo(() => {
        return [40, 100, 300, 1200];
    }, []);


    const calcScore = useCallback(() => {
        // if have score
        if (rowsCleared > 0) {
            // original tetrigs game score calculated
            dispatch(actions.setScore(prev => prev + linePoints[rowsCleared - 1] * (tetris.level + 1)));
            dispatch(actions.setRows(prev => prev + rowsCleared));
        }
    }, [tetris.level, linePoints, rowsCleared, dispatch]);

    useEffect(() => {
        calcScore();
    }, [calcScore, rowsCleared, tetris.score]);

    // return set-- for restarting game
    return [];
};
