import { combineReducers } from 'redux';

import { 
    SET_SCORE, 
    SET_ROWS, 
    SET_LEVEL, 
    SET_DROPTIME, 
    SET_GAMEOVER, 
    SET_ISIE,
    SET_BTNTITLE,
    SET_CLOSEIEALERT
 } from '../_actions/types';

 import { checkIsIE, closeIEAlert} from '../settingGame';

 const initialState = {
    score: 0,
    rows: 0,
    level: 0,
    dropTime: null,
    gameOver: false,
    btnTitle: 'Start Game',
    isIE: checkIsIE(),
    closeIEAlert: closeIEAlert()
  }

const tetris = (state = initialState, action) => {
    switch(action.type){
        case SET_SCORE:
            return {...state, score: action.payload}
        case SET_ROWS:
            return { ...state, rows: action.payload}
        case SET_LEVEL:
            return {...state, level: action.payload}
        case SET_DROPTIME:
            return {...state, dropTime: action.payload}
        case SET_GAMEOVER:
            return {...state, gameOver: action.payload}
        case SET_BTNTITLE:
            return {...state, btnTitle: action.payload}
        case SET_ISIE:
            return {...state, isIE: action.payload}
        case SET_CLOSEIEALERT:
            return {...state, closeIEAlert: action.payload}
        default:
            return state;
    }
}

const tetrisReducer = combineReducers({
    tetris,
});

export default tetrisReducer;
