import { useState, useEffect, useCallback, useMemo } from 'react';

import { useDispatch } from 'react-redux';
import * as actions from '../_actions/index';

export const useGameValues = rowsCleared => {
    const [score, setScore] = useState(0);
    const [rows, setRows] = useState(0);
    const [level, setLevel] = useState(0);

    const dispatch = useDispatch();

    // original tetris game score
    const linePoints = useMemo(() => {
        return [40, 100, 300, 1200];
    }, []);


    const calcScore = useCallback(() => {
        // if have score
        if (rowsCleared > 0) {
            // original tetrigs game score calculated
            dispatch(actions.setScore(prev => prev + linePoints[rowsCleared - 1] * (level + 1)));
            dispatch(actions.setRows(prev => prev + rowsCleared));
        }
    }, [tetris.level, linePoints, rowsCleared, dispatch]);

    useEffect(() => {
        calcScore();
    }, [calcScore, rowsCleared, score]);

    // return set-- for restarting game
    return [];
};
