import { useState, useCallback } from 'react';
import { CANVAS_WIDTH } from '../settingGame';
import { TETROMINOS, setBlock } from '../tetrominos';

export const useTentromino = () => {
    // set default
    const[tentromino, setTentromino] = useState({
        pos: {x:0, y:0},
        tetromino: TETROMINOS[0].shape,
        collided: false
    });

    
    const updateTentrominoPos = ({x, y, collided}) => {
        setTentromino(prev => ({
            ...prev,
            pos:{x:(prev.pos.x += x), y:(prev.pos.y += y)},
            collided,
        }));
    }

    // reset
    const resetTentromino = useCallback(() => {
        setTentromino({
            pos: {x:((CANVAS_WIDTH / 2 ) - 2), y:0},
            tetromino: setBlock().shape,
            collided: false
        })
    }, []);

    return [tentromino, updateTentrominoPos, resetTentromino];
}
